type DateFormat =
  | "dd-MMM-yyyy" //01-Feb-2025
  | "yyyy-MMM-dd" //2025-Feb-01
  | "yyyy-MM-ddZhh:mm:ss" //2025-02-01T13:45:30
  | "yyyy-MM-dd" //2025-02-01
  | "LLL d, yyyy t" //Feb 1, 2025 1:45 PM
  | "LLLL d, yyyy" //February 1, 2025
  | "LLL d, yyyy" //Feb 1, 2025
  | "t" //1:45 PM
